mapper.select:
SELECT m.id, m.name, m.url, m.icon, m.type, m.status, m.chinese_name, m.english_name, m.parent_id,
m.sort, m.update_time, m.create_time
FROM sys_menu m


        LEFT JOIN sys_menu_role mr on m.id = mr.menu_id
        WHERE mr.role_id = #{roleId}

            AND m.type in

                #{type}



            AND m.status = #{status}



        WHERE 1 = 1

            AND m.type in

                #{type}



            AND m.status = #{status}
